        "//\n"
        "//                                        ,,                \n"
        "//     generated by                mm   `7MM                \n"
        "//                                 MM     MM                \n"
        "//    ,pP\"Ybd  ,p6\"bo `7M'   `MF'mmMMmm   MMpMMMb.  .gP\"Ya  \n"
        "//    8I   `\" 6M'  OO   VA   ,V    MM     MM    MM ,M'   Yb \n"
        "//    `YMMMa. 8M         VA ,V     MM     MM    MM 8M\"\"\"\"\"\" \n"
        "//    L.   I8 YM.    ,    VVV      MM     MM    MM YM.    , \n"
        "//    M9mmmP'  YMbmd'     ,V       `Mbmo.JMML  JMML.`Mbmmd' \n"
        "//                       ,V                                 \n"
        "//                    OOb\"                                \n"
        "//\n\n";

static Array programNodes;

typedef struct
{
    AST ast;
    Array dependencies;
    char* path;
    char* moduleName;
    bool builtIn;
    bool searched;
} ProgramNode;

typedef struct
{
    ProgramNode* node;
    int importLineNumber;
    bool publicImport;
} ProgramDependency;

static char* GetFileString(const char* path)
{
    FILE* file = fopen(path, "rb");
    if (file == NULL)
        return NULL;

    fseek(file, 0, SEEK_END);
    const long fileSize = ftell(file);
    assert(fileSize != -1L);
    rewind(file);

    char* string = malloc(fileSize + 1);

    fread(string, sizeof(char), fileSize, file);
    fclose(file);
    string[fileSize] = '\0';
    return string;
}

static void FreeProgramTree()
{
    for (int i = 0; i < programNodes.length; ++i)
    {
        ProgramNode* node = *(ProgramNode**)programNodes.array[i];
        FreeArray(&node->dependencies);
        free(node->path);
        free(node->moduleName);
        free(node);
    }
    FreeArray(&programNodes);
}

static void HandleError(const Result result, const char* errorStage, const char* filePath)
{
    if (result.success)
    {
        assert(!result.hasError);
        return;
    }

    assert(result.hasError);
    printf("%s error: ", errorStage);
    PrintError(result);
    if (filePath) printf(" (%s)", filePath);
    printf("\n");
    printf("Press ENTER to continue...\n");
    getchar();
    exit(1);
}

static Result WriteOutputFile(const char* path, const char* code, const size_t length)
{
    FILE* file = fopen(path, "wb");
    if (file == NULL)
    {
        fclose(file);
        return ERROR_RESULT(AllocateString2Str("Failed to open output file \"%s\": %s", path, strerror(errno)), -1);
    }

    if (length == 0)
    {
        fclose(file);
        return SUCCESS_RESULT;
    }

    fwrite(watermark, sizeof(uint8_t), strlen(watermark), file);
    fwrite(code, sizeof(uint8_t), length, file);
    if (ferror(file))
        return ERROR_RESULT(AllocateString1Str("Failed to write to output file \"%s\"", path), -1);

    fclose(file);
    return SUCCESS_RESULT;
}

static Result IsFileOpenable(const char* path, const int lineNumber)
{
    FILE* file = fopen(path, "rb");

    if (file == NULL)
        return ERROR_RESULT(
            AllocateString2Str("Failed to open input file \"%s\": %s", path, strerror(errno)),
            lineNumber);

    fclose(file);

    return SUCCESS_RESULT;
}

static Result GetSourceFromImportPath(const char* path, const int lineNumber, char** outString)
{
    *outString = GetFileString(path);
    if (*outString == NULL)
        return ERROR_RESULT(
            AllocateString2Str("Failed to open input file \"%s\": %s", path, strerror(errno)),
            lineNumber);

    return SUCCESS_RESULT;
}

static Result CheckForCircularDependency(const ProgramNode* node, const ProgramNode* importedNode, const int lineNumber)
{
    for (int i = 0; i < importedNode->dependencies.length; ++i)
    {
        const ProgramDependency* dependency = importedNode->dependencies.array[i];

        if (dependency->node == node)
            return ERROR_RESULT("Circular dependency detected", lineNumber);

        const Result result = CheckForCircularDependency(node, dependency->node, lineNumber);
        if (result.hasError) return result;
    }

    return SUCCESS_RESULT;
}

static bool IsSameFileOrBuiltInPath(
    const bool isBuiltIn,
    const char* path,
    const ProgramNode* otherNode,
    const int lineNumber,
    const char* containingPath)
{
    if (isBuiltIn || otherNode->builtIn)
        return strcmp(path, otherNode->path) == 0;

    const int isSameFile = IsSameFile(path, otherNode->path);
    if (isSameFile == -1)
        HandleError(ERROR_RESULT("Could not compare file paths", lineNumber),
                    "Import", containingPath);
    return isSameFile;
}

char* AllocBaseFileName(const char* path)
{
    const size_t length = strlen(path) + 1;
    char* string = malloc(length);
    memcpy(string, path, length);

    char* fileName = basename(string);

    const size_t baseNameLength = strlen(fileName) + 1;
    for (int i = 0; i < baseNameLength; ++i)
        if (fileName[i] == '.') fileName[i] = '\0';

    return fileName;
}

static void AddBuiltInImport(AST* ast)
{
    const NodePtr builtInImport = AllocASTNode(
        &(ImportStmt)
        {
            .path = AllocateString("jsfx"),
            .moduleName = NULL,
            .public = false,
            .lineNumber = -1,
        },
        sizeof(ImportStmt), Node_Import);
    ArrayInsert(&ast->nodes, &builtInImport, 0);
}

static ProgramNode* GenerateProgramNode(
    const char* path,
    const char* moduleName,
    const int importLineNumber,
    const char* containingPath)
{
    const char* builtInSource = GetBuiltInSource(path);
    const bool isBuiltIn = builtInSource != NULL;

    if (!isBuiltIn)
        HandleError(IsFileOpenable(path, importLineNumber),
                    "Import", containingPath);

    for (int i = 0; i < programNodes.length; ++i)
    {
        ProgramNode* node = *(ProgramNode**)programNodes.array[i];
        const bool isSameNode = IsSameFileOrBuiltInPath(isBuiltIn, path, node, importLineNumber, containingPath);
        if (isSameNode)
            return node;
    }

    ProgramNode* programNode = malloc(sizeof(ProgramNode));
    ArrayAdd(&programNodes, &programNode);

    programNode->path = AllocateString(path);
    programNode->moduleName = moduleName != NULL ? AllocateString(moduleName) : AllocBaseFileName(path);
    programNode->builtIn = isBuiltIn;

    const char* source = NULL;
    if (!isBuiltIn)
        HandleError(GetSourceFromImportPath(path, importLineNumber, (char**)&source),
                    "Read", containingPath);
    else
        source = builtInSource;

    Array tokens;
    HandleError(Scan(source, &tokens),
                "Scan", path);
    if (!isBuiltIn) free((char*)source);

    HandleError(Parse(&tokens, &programNode->ast),
                "Parse", path);
    FreeTokenArray(&tokens);

    if (!isBuiltIn)
        AddBuiltInImport(&programNode->ast);

    programNode->dependencies = AllocateArray(sizeof(ProgramDependency));
    for (int i = 0; i < programNode->ast.nodes.length; ++i)
    {
        const NodePtr* node = programNode->ast.nodes.array[i];
        if (node->type != Node_Import) break;
        ImportStmt* importStmt = node->ptr;

        importStmt->moduleName = AllocBaseFileName(importStmt->path);

        ProgramDependency dependency =
        {
            .node = GenerateProgramNode(importStmt->path, importStmt->moduleName, importStmt->lineNumber, path),
            .importLineNumber = importStmt->lineNumber,
            .publicImport = importStmt->public,
        };
        ArrayAdd(&programNode->dependencies, &dependency);

        HandleError(CheckForCircularDependency(programNode, dependency.node, importStmt->lineNumber),
                    "Import", path);
    }

    programNode->searched = false;
    return programNode;
}

static void SortVisit(ProgramNode* node, Array* array)
{
    if (node->searched == true)
        return;

    for (int i = 0; i < node->dependencies.length; ++i)
        SortVisit(((ProgramDependency*)node->dependencies.array[i])->node, array);

    node->searched = true;
    ArrayAdd(array, &node);
}

static Array SortProgramTree()
{
    Array array = AllocateArray(sizeof(ProgramNode*));
    for (int i = 0; i < programNodes.length; ++i)
    {
        ProgramNode* node = *(ProgramNode**)programNodes.array[i];
        if (node->searched == 0)
            SortVisit(node, &array);
    }
    return array;
}

void CompileProgramTree(char** outCode, size_t* outLength)
{
    AST merged = {.nodes = AllocateArray(sizeof(NodePtr))};

    Map moduleNames = AllocateMap(0);
    const Array programNodes = SortProgramTree();
    for (int i = 0; i < programNodes.length; ++i)
    {
        const ProgramNode* node = *(ProgramNode**)programNodes.array[i];

        if (!MapAdd(&moduleNames, node->moduleName, NULL))
            HandleError(ERROR_RESULT(AllocateString1Str("Module \"%s\" is already defined", node->moduleName), -1),
                        "Import", node->path);

        const NodePtr module = AllocASTNode(
            &(ModuleNode)
            {
                .path = AllocateString(node->path),
                .moduleName = AllocateString(node->moduleName),
                .statements = node->ast.nodes,
            },
            sizeof(ModuleNode), Node_Module);
        ArrayAdd(&merged.nodes, &module);
    }
    FreeArray(&programNodes);
    FreeMap(&moduleNames);

    HandleError(GenerateCode(&merged, outCode, outLength),
                "Code generation", NULL);

    FreeAST(merged);
}

void Compile(const char* inputPath, const char* outputPath)
{
    programNodes = AllocateArray(sizeof(ProgramNode*));
    GenerateProgramNode(inputPath, NULL, -1, NULL);

    char* outputCode = NULL;
    size_t outputCodeLength = 0;
    CompileProgramTree(&outputCode, &outputCodeLength);

    FreeProgramTree();

    printf("%.*s\n", (int)outputCodeLength, outputCode);
    HandleError(WriteOutputFile(outputPath, outputCode, outputCodeLength),
                "Write", NULL);

    free(outputCode);
}
